  if (store.isLoading) {
    return <div>Loading...</div>;
  }

  if (!store.isAuth) {
    return <LoginForm />;
  }

  return (
    <div>
      <h1>
        {store.isAuth
          ? `User authenticated  ${store.user.email}`
          : 'PLEASE LOG IN'}
      </h1>
      <button onClick={() => store.logout()}>Log out</button>
      <div>
        <button>Get users</button>
      </div>
    </div>
  );
}

export default observer(App);
